<script type="text/javascript">
    (function ($) {
        $(document).ready(function () {
            $('.icon-classes-showcase i').on('click', function () {
                var class_name = $(this).attr('class');
                $('#icon_class').val(class_name);
                $('.show-icon').html('<i class="'+class_name+'" style="font-size: 32px;margin-left:5px;;"></i>');
                $('#icon').hide().val('');
                $('#icon_modal').modal('toggle');
            });
        });
    })(jQuery)
</script>
